import React, {useState, useEffect} from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import InputItem from '../InputItem/InputItem';
import ItemList from '../ItemList/ItemList';
import Footer from '../Footer/Footer';
import styles from './App.module.css';

import Todo from '../Todo/Todo';
import About from '../About/About';
import Contacts from '..Contacts/Contacts';

const App = () => {
  (<Router>
    <div className = {styles.base}>
      <Card className = {styles.sidebar}>
        <MenuList>
          <Link to='/' className = {styles.link}><MenuItem>About me</MenuItem></Link>
          <Link to='/todo' className = {styles.link}><MenuItem>Tasks</MenuItem></Link>
          <Link to='/contacts' className = {styles.link}><MenuItem>Contacts</MenuItem></Link>
        </MenuList>
      </Card>

      <Card className = {styles.content}>
        <Route path='/' exact component = {About} />
        <Route path='/todo' component={Todo} />
        <Route path='/contacts' component={Contacts} />
      </Card>

    </div>
  </Router>);

    const Todo = () => {
      const initialState = {
      items: [
        {
          value: 'Create a new application.',
          isDone: false,
          id: 1
        },
        {
          value: 'Talk to my friend.',
          isDone: true,
          id: 2
        },
        {
          value: 'Make one more step closer to my goal.',
          isDone: true,
          id: 3
        }
      ],
      count: 3
    };

    const [items, setItems] = useState (initialState.items);
    const [count, setCount] = useState (initialState.count);

    useEffect(() => {console.log('mount');}, []);
    useEffect(() => {console.log('update');});

    const onClickDone = id => {
      const newItemList = items.map(item => {
        const newItem = { ...item };
        if (item.id === id) {
          newItem.isDone = !item.isDone;
        }
        return newItem;
      });
      setItems(newItemList);
    };


    const onClickDelete = id => {
      const newItemList = items.filter(item =>{
      return item.id !== id; 
        });
        setItems(newItemList)
        setCount(count - 1)
    };

      const onClickAdd = value => {
        setItems (
          [...items,
            {
              value,
              isDone: false,
              id: count + 1
        }]);
        setCount(count + 1)
      }



      return (
        <div className = {styles.base}>
          <h1 className = {styles.title}>Important tasks: </h1>
          <InputItem onClickAdd = {onClickAdd} /> 
          <ItemList 
          items = {items} 
          onClickDone={onClickDone} 
          onClickDelete={onClickDelete}/>
          <Footer count = {count} />
        </div>);
  }
}
export default App;
